mapper.select:
SELECT count(*) as allCnt, TRUNCATE(count(*) / #{viewPageCnt}, 0) + 1 as lastPage from article A
is_delete = false
AND A.seq = #{seq}
AND A.master_seq = #{master_seq}
AND (A.title like '%${search}%' or A.content like '%${search}%')

WITH RECURSIVE comment_hierarchy AS ( SELECT A.seq, A.article_seq, A.parent_comment_seq, A.content, A.user_seq, IFNULL((select SUM(IF(AA.is_up, 1, -1)) from gravitygather.likes AA where AA.mode = 'comment' and AA.content_seq = A.seq), 0) as likes,
(select AA.is_up from gravitygather.likes AA where AA.mode = 'comment' and AA.content_seq = A.seq and AA.user_seq = #{user_seq}) as is_up,
A.is_delete, A.created_at, A.updated_at, B.photo, B.nickname, A.seq AS root_seq FROM gravitygather.comment A LEFT JOIN gravitygather.users B ON A.user_seq = B.seq
A.parent_comment_seq IS NULL
AND A.seq = #{seq}
AND A.article_seq = #{article_seq}
UNION ALL SELECT A.seq, A.article_seq, A.parent_comment_seq, A.content, A.user_seq, IFNULL((select SUM(IF(AA.is_up, 1, -1)) from gravitygather.likes AA where AA.mode = 'comment' and AA.content_seq = A.seq), 0) as likes,
(select AA.is_up from gravitygather.likes AA where AA.mode = 'comment' and AA.content_seq = A.seq and AA.user_seq = #{user_seq}) as is_up,
A.is_delete, A.created_at, A.updated_at, B.photo, B.nickname, CH.root_seq FROM gravitygather.comment A LEFT JOIN gravitygather.users B ON A.user_seq = B.seq JOIN comment_hierarchy CH ON A.parent_comment_seq = CH.seq
A.seq = #{seq}
A.article_seq = #{article_seq}
) SELECT * FROM comment_hierarchy ORDER BY root_seq, seq

mapper.update:
update article set
   master_seq = #{master_seq},
   title = #{title},
   content = #{content}
where
    seq = #{seq}

mapper.select:
SELECT
    mode,
    content_seq,
    user_seq,
    is_up,
    created_at
from Likes


        mode = #{mode}


        and content_seq = #{content_seq}


        and user_seq = #{user_seq}

mapper.delete:
delete from likes
where
    mode = #{mode}
and user_seq = #{user_seq}
and content_seq = #{content_seq}

delete from scrap
where
    article_seq = #{seq}
and user_seq = #{user_seq}

mapper.insert:
insert into scrap(
    article_seq,
    user_seq
) values (
    #{seq},
    #{user_seq}
)
